    }

    return (
        <>
            <h1>WELLCOME {firstName} {lastName}</h1>
            <input type="button" value="Change FirstName" onClick={changeFn}/>
            <input type="button" value="Change LastName" onClick={changeLn}/>
        </>
    )
}
